// Base64 encoded short audio clips for better portability
const SOUNDS = {
  correct: 'data:audio/mp3;base64,',
  incorrect: 'data:audio/mp3;base64,',
  tick: 'data:audio/mp3;base64,',
  victory: 'data:audio/mp3;base64,'
};

class SoundManager {
  private audio: HTMLAudioElement;
  private isMuted: boolean = false;

  constructor() {
    this.audio = new Audio();
    // Try to initialize with a silent sound
    this.audio.volume = 0;
    this.audio.src = SOUNDS.tick;
  }

  playSound(soundType: keyof typeof SOUNDS) {
    if (this.isMuted) return;
    
    try {
      this.audio.volume = 1;
      this.audio.src = SOUNDS[soundType];
      this.audio.play().catch(() => {
        // Ignore errors - audio might not play due to browser restrictions
        console.log('Audio playback failed');
      });
    } catch (error) {
      console.log('Error playing sound:', error);
    }
  }

  toggleMute() {
    this.isMuted = !this.isMuted;
    return this.isMuted;
  }

  setMute(mute: boolean) {
    this.isMuted = mute;
  }
}

export const soundManager = new SoundManager(); 